mapper.update:
update sabang set
sabang_name=#{sabang_name}, sabang_price=#{sabang_price}, sabang_saleprice=#{sabang_saleprice}, sabang_buycount=#{sabang_buycount}, sabang_viewcount=#{sabang_viewcount}, sabang_state=#{sabang_state}
sabang_name=#{sabang_name}, sabang_price=#{sabang_price}, sabang_saleprice=#{sabang_saleprice}, sabang_buycount=#{sabang_buycount}, sabang_viewcount=#{sabang_viewcount}, sabang_imgoname=#{sabang_imgoname}, sabang_imgsname=#{sabang_imgsname}, sabang_imgtype=#{sabang_imgtype}, sabang_state=#{sabang_state}
where sabang_id=#{sabang_id}

mapper.select:
select rnum, sabang_id, sabang_name, sabang_price,
sabang_saleprice, sabang_buycount, sabang_viewcount,
sabang_imgoname, sabang_imgsname, sabang_imgtype, sabang_state
from (
	select rownum as rnum, sabang_id, sabang_name, sabang_price,
	sabang_saleprice, sabang_buycount, sabang_viewcount,
	sabang_imgoname, sabang_imgsname, sabang_imgtype, sabang_state
	from (
		select sabang_id, sabang_name, sabang_price,
		sabang_saleprice, sabang_buycount, sabang_viewcount,
		sabang_imgoname, sabang_imgsname, sabang_imgtype, sabang_state
		from sabang
		order by sabang_buycount desc
	)
	where rownum <= #{endRowNo}
)
where rnum >= #{startRowNo}

